mapper.update:
UPDATE event SET event_title = #{event_title}
	,user_id = #{user_id}
	,event_content = #{event_content}
	,event_start_date = #{event_start_date}
	,event_end_date = #{event_end_date}
	WHERE event_id = #{event_id}

mapper.select:
